mapper.update:
update fb_matches set qt_id = #{qtId,jdbcType=BIGINT}, season_id = #{seasonId,jdbcType=BIGINT}, league_id = #{leagueId,jdbcType=BIGINT}, league_name = #{leagueName,jdbcType=VARCHAR}, home_team_id = #{hid,jdbcType=BIGINT}, home_team_name = #{hn,jdbcType=VARCHAR}, guest_team_id = #{gid,jdbcType=BIGINT}, guest_team_name = #{gn,jdbcType=VARCHAR}, match_type = #{matchType,jdbcType=INTEGER},
home_rank = #{homeRank,jdbcType=VARCHAR},
guest_rank = #{guestRank,jdbcType=VARCHAR},
group_name = #{groupName,jdbcType=VARCHAR},
match_time = #{matchTime,jdbcType=TIMESTAMP}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_time = #{updateTime,jdbcType=TIMESTAMP}
where qt_id = #{qtId,jdbcType=BIGINT}

update fb_matches 

	
		full_score = #{fullScore},
	
	
		half_score = #{halfScore},
	
		match_type = #{matchType}
 
 where qt_id = #{qtId}
